import React from 'react';
import tripListData from './TripListData.js'

import Container from '@material-ui/core/Container';

export default function Description ({tripListData})  {
  const classes = useStyles();

  React.useEffect(() => {
    loadCSS(
      'https://use.fontawesome.com/releases/v5.12.0/css/all.css',
      document.querySelector('#font-awesome-css'),
    );
  }, []);

  return (
    <div className={classes.root}>
      <Container maxWidth="sm" > 

          <div>For {tripListData.numberOfPeople} 
            {tripListData.numberOfPeople === "single" ? <i class="fas fa-user-alt"></i> : null }
            {tripListData.numberOfPeople === "couple" ? <i class="fas fa-user-friends"></i> : null }
            {tripListData.numberOfPeople === "family with kids" ? <i class="fad fa-users"></i> : null }
          </div> 
          <div>During {tripListData.tripLength}</div> 
          <div>In the {tripListData.seasonTime}</div>
      </Container>
      </div>
    )
  }
