import React from 'react';
import tripListData from './TripListData.js'

import Container from '@material-ui/core/Container';

import PersonIcon from '@material-ui/icons/Person';
import PeopleIcon from '@material-ui/icons/People';
import ChildFriendlyIcon from '@material-ui/icons/ChildFriendly';

import WbSunnyIcon from '@material-ui/icons/WbSunny';

export default function Description ({tripListData})  {


  return (
      <Container maxWidth="sm" > 
          <div>For {tripListData.numberOfPeople} 
          import WbSunnyIcon from '@material-ui/icons/WbSunny';
            {tripListData.numberOfPeople === "family with kids" ? <ChildFriendlyIcon /> : null }
          </div> 
          <div>During {tripListData.tripLength}</div> 
          <div>In the {tripListData.seasonTime}</div>
      </Container>
    )
  }
